  --destructive-foreground: hsl(0, 0%, 98%);
  --ring: hsl(240, 4.9%, 83.9%);
}

@layer base {
  * {
    @apply border-border;
  }

  body {
    @apply font-sans antialiased bg-background text-foreground;
  }
}

@layer utilities {
  .text-primary-cyan {
    color: hsl(186, 100%, 60%);
  }

  .bg-primary-cyan {
    background-color: hsl(186, 100%, 60%);
  }

  .text-accent-magenta {
    color: hsl(0, 85%, 67%);
  }

  .bg-accent-magenta {
    background-color: hsl(0, 85%, 67%);
  }

  .neon-glow {
    animation: neon-glow 3s ease-in-out infinite;
  }

  .lens-flare {
    animation: lens-flare 4s ease-in-out infinite;
  }

  .animate-typewriter {
    overflow: hidden;
    border-right: 2px solid white;
    white-space: nowrap;
    animation: typewriter 4s steps(40) 1s forwards, blink 0.75s step-end infinite;
  }

  .animate-float {
    animation: float 3s ease-in-out infinite;
  }

  .animate-pulse-slow {
    animation: pulse 3s cubic-bezier(0.4, 0, 0.6, 1) infinite;
  }

  .animate-slide-up {
    animation: slideUp 0.5s ease-out forwards;
  }

  .animate-fade-in {
    animation: fadeIn 0.6s ease-out forwards;
  }

  .animate-bounce-in {
    animation: bounceIn 0.6s ease-out forwards;
  }

  .animate-scale-in {
    animation: scaleIn 0.3s ease-out forwards;
  }

  .animate-shimmer {
    animation: shimmer 2s infinite linear;
  }

  .animate-glow {
    animation: glow 2s ease-in-out infinite alternate;
  }

  .animate-wave {
    animation: wave 3s ease-in-out infinite;
  }

  .animate-rotate-slow {
    animation: rotateSlow 20s linear infinite;
  }

  .animate-gradient {
    animation: gradient 3s ease infinite;
  }
}

@keyframes typewriter {
  0% {
    width: 0%;
  }
  100% {
    width: 100%;
  }
}

@keyframes blink {
  0%, 50% {
    border-color: transparent;
  }
  51%, 100% {
    border-color: white;
  }
}

@keyframes float {
  0%, 100% {
    transform: translateY(0px);
  }
  50% {
    transform: translateY(-10px);
  }
}

@keyframes slideUp {
  0% {
    transform: translateY(30px);
    opacity: 0;
  }
  100% {
    transform: translateY(0);
    opacity: 1;
  }
}

@keyframes fadeIn {
  0% {
    opacity: 0;
  }
  100% {
    opacity: 1;
  }
}

@keyframes bounceIn {
  0% {
    transform: scale(0.3);
    opacity: 0;
  }
  50% {
    transform: scale(1.05);
  }
  70% {
    transform: scale(0.9);
  }
  100% {
    transform: scale(1);
    opacity: 1;
  }
}

@keyframes scaleIn {
  0% {
    transform: scale(0.9);
    opacity: 0;
  }
  100% {
    transform: scale(1);
    opacity: 1;
  }
}

@keyframes shimmer {
  0% {
    background-position: -200% 0;
  }
  100% {
    background-position: 200% 0;
  }
}

@keyframes glow {
  0% {
    box-shadow: 0 0 20px rgba(34, 197, 94, 0.5);
  }
  100% {
    box-shadow: 0 0 30px rgba(34, 197, 94, 0.8), 0 0 40px rgba(34, 197, 94, 0.6);
  }
}

@keyframes wave {
  0%, 100% {
    transform: translateY(0px) rotate(0deg);
  }
  50% {
    transform: translateY(-10px) rotate(5deg);
  }
}

@keyframes rotateSlow {
  from {
    transform: rotate(0deg);
  }
  to {
    transform: rotate(360deg);
  }
}

@keyframes gradient {
  0% {
    background-position: 0% 50%;
  }
  50% {
    background-position: 100% 50%;
  }
  100% {
    background-position: 0% 50%;
  }
}

.gradient-hero {
  background: linear-gradient(135deg, hsl(220, 39%, 5%) 0%, hsl(220, 39%, 8%) 50%, hsl(220, 39%, 3%) 100%);
  background-size: 400% 400%;
}

.gradient-flash {
  background: linear-gradient(135deg, hsl(220, 39%, 8%) 0%, hsl(220, 39%, 11%) 50%, hsl(220, 39%, 6%) 100%);
  background-size: 400% 400%;
}

.gradient-animated {
  background: linear-gradient(-45deg, hsl(220, 39%, 5%), hsl(186, 100%, 10%), hsl(0, 85%, 10%), hsl(220, 39%, 8%));
  background-size: 400% 400%;
  animation: gradient 4s ease infinite;
}

.nebula-background {
  background: linear-gradient(135deg, hsl(220, 39%, 5%) 0%, hsl(220, 50%, 8%) 25%, hsl(186, 30%, 10%) 50%, hsl(0, 30%, 8%) 75%, hsl(220, 39%, 5%) 100%);
